function count1(clicked_id)
{
var counter = document.getElementById('counter1').innerHTML;
    var parsed = parseInt(counter);
    let result = clicked_id == "minus" ? parsed - 1 : parsed + 1;
    document.getElementById('counter1').innerHTML = result;
}

